mapper.select:
select
	*
from
	tag T join space_tag ST
	using(tag_no)
where ST.space_no = #{ spaceNo }

mapper.update:
update 
	review
set
	review_comment = #{content}
where
	review_no = #{reviewNo}

mapper.insert:
insert into
	space_option 
values (
	#{ spaceNo },
	#{ optionNo }
)

insert into
	space_tag 
values (
	#{ spaceNo },
	#{ tagNo }
)

mapper.update:
update 
	space
set
	star_avg = #{starAvg}
where
	space_no = #{spaceNo}
